// src/components/NavbarComponent.js
import React from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom'; // Importa useNavigate

// Asegúrate de que las props se desestructuren correctamente
const NavbarComponent = ({ user, onLogout }) => {
    const navigate = useNavigate();

    const handleLoginClick = () => {
        navigate('/login');
    };

    const handleLogoutClick = () => {
        onLogout(); // Llama a la función de logout pasada como prop
        navigate('/'); // Redirige al inicio después de cerrar sesión
    };

    // Línea 19 (ejemplo): Aquí es donde probablemente estás usando user.isLoggedIn
    // Debes verificar si 'user' existe y si 'user.isLoggedIn' es verdadero
    const isLoggedIn = user && user.isLoggedIn; // Esto maneja el caso donde user es null

    return (
        <Navbar bg="dark" variant="dark" expand="lg" className="shadow-sm">
            <Container>
                <Navbar.Brand as={Link} to="/">
                    <img
                        src="/pnk-logo.png" // Ruta a tu logo en la carpeta public
                        height="30"
                        className="d-inline-block align-top"
                        alt="PNK Inmobiliaria Logo"
                    />
                    PNK Inmobiliaria
                </Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        <Nav.Link as={Link} to="/">Inicio</Nav.Link>
                        <Nav.Link as={Link} to="/propiedades">Propiedades</Nav.Link>
                        <Nav.Link as={Link} to="/publicar">Publicar</Nav.Link>
                        <Nav.Link as={Link} to="/registrarse">Registrarse</Nav.Link>
                    </Nav>
                    <Nav>
                        {isLoggedIn ? ( // Usa la variable isLoggedIn
                            <>
                                <Nav.Link>{user.nombre_usuario || 'Usuario'}</Nav.Link> {/* Muestra el nombre de usuario */}
                                <Button variant="outline-light" onClick={handleLogoutClick}>
                                    Cerrar Sesión
                                </Button>
                                {/* Puedes añadir un link para 'Admin' si el usuario es administrador */}
                                {user.rol === 'admin' && ( // Asumiendo que el objeto 'user' tiene un 'rol'
                                    <Nav.Link as={Link} to="/admin">Admin</Nav.Link>
                                )}
                            </>
                        ) : (
                            <>
                                <Button variant="primary" onClick={handleLoginClick} className="me-2">
                                    Iniciar Sesión
                                </Button>
                                <Nav.Link as={Link} to="/recuperar-contrasena">Recuperar Contraseña</Nav.Link>
                                {/* Si 'Admin' es solo para usuarios no logueados que quieren acceder */}
                                <Nav.Link as={Link} to="/admin">Admin</Nav.Link>
                            </>
                        )}
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
};

export default NavbarComponent;
