// src/App.js
import React, { useState, useEffect } from 'react';
// IMPORTANTE: Ya no importamos 'BrowserRouter as Router' aquí,
// porque el BrowserRouter principal ya está en index.js
import { Routes, Route } from 'react-router-dom';

// Importa tus componentes
import NavbarComponent from './components/NavbarComponent';
import FooterComponent from './components/FooterComponent';
import Login from './components/Login.jsx';

// Asegúrate de que esta línea sea EXACTAMENTE así (PropertiesList PLURAL)
import PropertiesList from './components/Properties/PropertiesList';

function App() {
    const [user, setUser] = useState(null);

    const handleLoginSuccess = (userData) => {
        setUser(userData);
        localStorage.setItem('user', JSON.stringify(userData));
    };

    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        }
    }, []);

    const handleLogout = () => {
        setUser(null);
        localStorage.removeItem('user');
    };

    return (
        // Usamos un Fragment (<></>) porque ya no hay un <Router> aquí.
        // El único <BrowserRouter> está en index.js envolviendo toda la App.
        <>
            <NavbarComponent user={user} onLogout={handleLogout} />
            <main>
                <Routes>
                    <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />

                    {/* Asegúrate de que aquí también sea PropertiesList (PLURAL) */}
                    <Route path="/" element={<PropertiesList />} />
                    <Route path="/propiedades" element={<PropertiesList />} />

                    {/* Agrega otras rutas si las tienes */}
                </Routes>
            </main>
            <FooterComponent />
        </>
    );
}

export default App;
