// src/App.js
import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';

import NavbarComponent from './components/NavbarComponent';
import FooterComponent from './components/FooterComponent'; // Asegúrate de que esta importación sea correcta
import Login from './components/Login.jsx';

import PropertiesList from './components/Properties/PropertiesList';

function App() {
    const [user, setUser] = useState(null);

    const handleLoginSuccess = (userData) => {
        setUser(userData);
        localStorage.setItem('user', JSON.stringify(userData));
    };

    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        }
    }, []);

    const handleLogout = () => {
        setUser(null);
        localStorage.removeItem('user');
    };

    return (
        <>
            <NavbarComponent user={user} onLogout={handleLogout} />
            <main>
                <Routes>
                    <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />
                    <Route path="/" element={<PropertiesList />} />
                    <Route path="/propiedades" element={<PropertiesList />} />
                </Routes>
            </main>
            <FooterComponent user={user} /> {/* ¡AGREGA user={user} AQUÍ! */}
        </>
    );
}

export default App;
