// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Importa tus componentes
import NavbarComponent from './components/NavbarComponent';
import FooterComponent from './components/FooterComponent';
import Login from './components/Login.jsx';

// ¡IMPORTANTE! Asegúrate de que esta línea sea EXACTAMENTE así:
import PropertiesList from './components/Properties/PropertiesList'; // <--- PropertiesList (PLURAL)

function App() {
    const [user, setUser] = useState(null);

    const handleLoginSuccess = (userData) => {
        setUser(userData);
        localStorage.setItem('user', JSON.stringify(userData));
    };

    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        }
    }, []);

    const handleLogout = () => {
        setUser(null);
        localStorage.removeItem('user');
    };

    return (
        <Router>
            <NavbarComponent user={user} onLogout={handleLogout} />
            <main>
                <Routes>
                    <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />

                    {/* ¡IMPORTANTE! Asegúrate de que aquí también sea PropertiesList (PLURAL) */}
                    <Route path="/" element={<PropertiesList />} />
                    <Route path="/propiedades" element={<PropertiesList />} />

                    {/* Agrega otras rutas si las tienes */}
                </Routes>
            </main>
            <FooterComponent />
        </Router>
    );
}

export default App;
