import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Importar componentes de UI (estos no cambian)
import NavbarComponent from './components/NavbarComponent';
import HeroSection from './components/HeroSection';
import FeaturedProperties from './components/FeaturedProperties';
import ServicesSection from './components/ServicesSection';
import FooterComponent from './components/FooterComponent';

// =========================================================================
// !!! AJUSTES DE IMPORTACIÓN BASADOS EN TU ESTRUCTURA DE CARPETAS (image_627b61.png) !!!
// =========================================================================

// Importar el componente de login que interactúa con la API
import LoginPage from './pages/LoginPage'; // <--- CORREGIDO: Ahora apunta a src/pages/LoginPage.js

// Importar el componente de recuperación de contraseña
import Recovery from './components/Recovery'; // Asumiendo que Recovery.js sigue en src/components/

// Importaciones para los componentes de Dashboard
// Basado en tu estructura, están todos en src/pages/
import AdminDashboard from './pages/AdminDashboard';
import Dashboard from './pages/Dashboard';
import GestorDashboard from './pages/GestorDashboard';
import PropietarioDashboard from './pages/PropietarioDashboard';

// Puedes dejar el 'Login.js' en src/components/ si no lo usas,
// pero si es el antiguo login PHP o no lo necesitas más, puedes eliminar el archivo
// o simplemente no importarlo aquí si no lo vas a usar en ninguna ruta.
// import Login from './components/Login'; // <--- Si no lo usas, puedes comentarlo o borrarlo.

// =========================================================================
// FIN DE AJUSTES DE IMPORTACIÓN
// =========================================================================


function App() {
  const [session, setSession] = useState({
    isLoggedIn: false,
    userName: '',
    userType: ''
  });

  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        const user = JSON.parse(storedUser);
        setSession({
          isLoggedIn: true,
          userName: user.nombre,
          userType: user.tipo_usuario
        });
      } catch (e) {
        console.error("Error parsing stored user data:", e);
        localStorage.removeItem('user');
      }
    }
  }, []);

  const handleLoginSuccess = (userData) => {
    setSession({
      isLoggedIn: true,
      userName: userData.nombre,
      userType: userData.tipo_usuario
    });
    localStorage.setItem('user', JSON.stringify(userData));

    switch (userData.tipo_usuario) {
        case 'admin':
            navigate('/admin/dashboard');
            break;
        case 'gestor':
            navigate('/gestor/dashboard');
            break;
        case 'propietario':
            navigate('/propietario/dashboard');
            break;
        default:
            navigate('/dashboard');
    }
  };

  const handleLogout = () => {
    setSession({ isLoggedIn: false, userName: '', userType: '' });
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div className="App">
      <NavbarComponent session={session} onLogout={handleLogout} />
      <Routes>
        <Route path="/" element={
          <>
            <HeroSection />
            <FeaturedProperties />
            <ServicesSection />
          </>
        } />
        {/* Usamos LoginPage que está en src/pages/ */}
        <Route path="/login" element={<LoginPage onLoginSuccess={handleLoginSuccess} />} />
        <Route path="/recuperar" element={<Recovery />} />

        {/* Rutas de Dashboards - Ahora importadas correctamente desde src/pages/ */}
        <Route path="/admin/dashboard" element={<AdminDashboard />} />
        <Route path="/gestor/dashboard" element={<GestorDashboard />} />
        <Route path="/propietario/dashboard" element={<PropietarioDashboard />} />
        <Route path="/dashboard" element={<Dashboard />} />

        {/* Añade más rutas aquí según tus necesidades (ej. registro, detalle de propiedades) */}
        {/* <Route path="/registro" element={<RegisterPage />} /> */}
      </Routes>
      <FooterComponent session={session} />
    </div>
  );
}

export default App;
