import React from 'react';
import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const PropertyCard = ({ propiedad }) => {
    const formattedPrice = new Intl.NumberFormat('es-CL', { style: 'currency', currency: 'CLP', minimumFractionDigits: 0 }).format(propiedad.precio);

    return (
        <div className="col-md-4 mb-4">
            <Card className="property-card">
                <Card.Img variant="top" src={propiedad.imagen_url} alt={propiedad.titulo} className="property-image" />
                <Card.Body>
                    <Card.Title>{propiedad.titulo}</Card.Title>
                    <Card.Text>
                        {propiedad.descripcion.substring(0, 100)}{propiedad.descripcion.length > 100 ? '...' : ''}
                    </Card.Text>
                    <p className="property-price">{formattedPrice}</p>
                    <Button as={Link} to={`/detalle_inmuebles/${propiedad.id}`} variant="primary">Ver Detalles</Button>
                </Card.Body>
            </Card>
        </div>
    );
};

export default PropertyCard;
