import React, { useState } from 'react';
// ELIMINA: import { useNavigate } from 'react-router-dom'; // Ya no lo necesitas aquí

// Recibe onLoginSuccess como prop
const LoginPage = ({ onLoginSuccess }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    // ELIMINA: const navigate = useNavigate(); // Ya no lo necesitas aquí, App.js lo maneja

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const response = await fetch('http://18.218.217.229/api/login.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                },
                body: new URLSearchParams({
                    usuario: email,
                    clave: password,
                }).toString(),
            });

            if (!response.ok) {
                const errorData = await response.json();
                setError(errorData.message || 'Error al iniciar sesión. Inténtalo de nuevo.');
                return;
            }

            const data = await response.json();

            if (data.success) {
                console.log('Inicio de sesión exitoso:', data.user);
                // Llama a la función onLoginSuccess que viene de App.js
                onLoginSuccess(data.user); // Esto ahora manejará la redirección

            } else {
                setError(data.message || 'Credenciales incorrectas. Verifique su correo y contraseña.');
            }
        } catch (err) {
            console.error('Error de red o del servidor:', err);
            setError('No se pudo conectar con el servidor. Inténtalo de nuevo más tarde.');
        }
    };

    return (
        <div className="login-container">
            <div className="logo">
                <img src="/img/logo.png" alt="PNK Inmobiliaria Logo" height="80" />
            </div>
            <h2>Iniciar Sesión</h2>

            {error && <p className="error-message">{error}</p>}

            <form className="login-form" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="usuario">Correo Electrónico:</label>
                    <input
                        type="email"
                        id="usuario"
                        name="usuario"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="clave">Contraseña:</label>
                    <input
                        type="password"
                        id="clave"
                        name="clave"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Entrar</button>
            </form>
            <div className="register-link">
                ¿No tienes cuenta? <a href="/registro">Regístrate aquí</a> {/* ¡IMPORTANTE! Si esto es un enlace de React Router, DEBE ser <Link to="/registro"> */}
            </div>
        </div>
    );
};

export default LoginPage;
