import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Importar componentes
import NavbarComponent from './components/NavbarComponent';
import HeroSection from './components/HeroSection';
import FeaturedProperties from './components/FeaturedProperties';
import ServicesSection from './components/ServicesSection';
import FooterComponent from './components/FooterComponent';
import Login from './components/Login';
import Recovery from './components/Recovery';
// Añade aquí importaciones para otros componentes de rutas futuras (ej. Register, Dashboard, DetalleInmueble)

function App() {
  const [session, setSession] = useState({
    isLoggedIn: false,
    userName: '',
    userType: ''
  });

  useEffect(() => {
    // Aquí iría tu lógica para verificar si el usuario ya está logueado (ej. token en localStorage)
  }, []);

  const handleLoginSuccess = (userData) => {
    setSession({ isLoggedIn: true, userName: userData.name, userType: userData.type });
  };

  const handleLogout = () => {
    setSession({ isLoggedIn: false, userName: '', userType: '' });
    window.location.href = '/login';
  };

  return (
    <Router>
      <div className="App">
        <NavbarComponent session={session} onLogout={handleLogout} />
        <Routes>
          <Route path="/" element={
            <>
              <HeroSection />
              <FeaturedProperties />
              <ServicesSection />
            </>
          } />
          <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />
          <Route path="/recuperar" element={<Recovery />} />
          {/* Añade más rutas aquí según tus necesidades (ej. registro, dashboard, detalle de propiedades) */}
          {/* <Route path="/buscar_inmuebles" element={<BuscarInmuebles />} /> */}
          {/* <Route path="/publicar_inmueble" element={<PublicarInmueble />} /> */}
          {/* <Route path="/registrarme" element={<Registrarse />} /> */}
          {/* <Route path="/dashboard" element={<Dashboard />} /> */}
          {/* <Route path="/admin/dashboard_admin" element={<AdminDashboard />} /> */}
          {/* <Route path="/detalle_inmuebles/:id" element={<DetalleInmuebles />} /> */}
        </Routes>
        <FooterComponent session={session} />
      </div>
    </Router>
  );
}

export default App;
