// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Importa tus componentes
import NavbarComponent from './components/NavbarComponent'; // Si lo tienes
import FooterComponent from './components/FooterComponent'; // Si lo tienes
import Login from './components/Login.jsx'; // Ruta corregida: directo en components
import Property from './components/Properties/PropertyList.jsx'; // ¡Importa el nuevo componente!

function App() {
    const [user, setUser] = useState(null); // Para manejar el estado del usuario logueado

    const handleLoginSuccess = (userData) => {
        setUser(userData);
        // Opcional: Puedes guardar la sesión del usuario en localStorage o sessionStorage aquí
        localStorage.setItem('user', JSON.stringify(userData));
    };

    // Opcional: Para cargar el usuario de la sesión al inicio
    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        }
    }, []);

    // Función para manejar el logout (puedes pasarla al Navbar si tienes un botón de logout)
    const handleLogout = () => {
        setUser(null);
        localStorage.removeItem('user');
        // Redirigir a login o inicio si es necesario
        // navigate('/login');
    };


    return (
        <Router>
            {/* Pasa el usuario y la función de logout al Navbar si los necesita */}
            <NavbarComponent user={user} onLogout={handleLogout} />
            <main> {/* Un contenedor semántico para el contenido principal */}
                <Routes>
                    {/* Ruta para la página de login */}
                    <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />

                    {/* Ruta principal (raiz) donde se mostrará la lista de propiedades destacadas o todas */}
                    <Route path="/" element={<PropertiesList />} />

                    {/* Ruta específica para la página de propiedades (si no es la raíz) */}
                    <Route path="/propiedades" element={<PropertiesList />} />

                    {/* Agrega otras rutas de tu aplicación aquí (ej. /dashboard, /registro, /publicar) */}
                    {/* <Route path="/dashboard" element={<Dashboard />} /> */}
                    {/* <Route path="/registro" element={<Registro />} /> */}
                    {/* <Route path="/publicar" element={<PublicarInmueble />} /> */}
                </Routes>
            </main>
            <FooterComponent /> {/* Renderiza tu pie de página */}
        </Router>
    );
}

export default App;
