import React, { useState, useEffect } from 'react';
// Asegúrate de que BrowserRouter esté envolviendo <App /> en tu index.js
import { Routes, Route, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Importar componentes de UI
import NavbarComponent from './components/NavbarComponent';
import HeroSection from './components/HeroSection';
import FeaturedProperties from './components/FeaturedProperties'; // Este se mantendrá para propiedades destacadas (si las hay, o será un esqueleto)
import ServicesSection from './components/ServicesSection';
import FooterComponent from './components/FooterComponent';
import PropertiesList from './components/PropertiesList'; // ¡Nuevo componente para la lista completa!

// Importar el componente de login que interactúa con la API
import LoginPage from './pages/LoginPage';

// Importar el componente de recuperación de contraseña
import Recovery from './components/Recovery';

// Importaciones para los componentes de Dashboard
import AdminDashboard from './pages/AdminDashboard';
import Dashboard from './pages/Dashboard';
import GestorDashboard from './pages/GestorDashboard';
import PropietarioDashboard from './pages/PropietarioDashboard';

function App() {
  const [session, setSession] = useState({
    isLoggedIn: false,
    userName: '',
    userType: ''
  });

  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        const user = JSON.parse(storedUser);
        setSession({
          isLoggedIn: true,
          userName: user.nombre,
          userType: user.tipo_usuario
        });
        // Opcional: Redirigir al dashboard si ya está logueado al cargar la app
        // if (user.tipo_usuario === 'admin') navigate('/admin/dashboard');
        // else if (user.tipo_usuario === 'gestor') navigate('/gestor/dashboard');
        // else if (user.tipo_usuario === 'propietario') navigate('/propietario/dashboard');
        // else navigate('/dashboard');
      } catch (e) {
        console.error("Error parsing stored user data:", e);
        localStorage.removeItem('user');
      }
    }
  }, [navigate]);

  const handleLoginSuccess = (userData) => {
    setSession({
      isLoggedIn: true,
      userName: userData.nombre,
      userType: userData.tipo_usuario
    });
    localStorage.setItem('user', JSON.stringify(userData));

    switch (userData.tipo_usuario) {
      case 'admin':
        navigate('/admin/dashboard');
        break;
      case 'gestor':
        navigate('/gestor/dashboard');
        break;
      case 'propietario':
        navigate('/propietario/dashboard');
        break;
      default:
        navigate('/dashboard');
    }
  };

  const handleLogout = () => {
    setSession({ isLoggedIn: false, userName: '', userType: '' });
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div className="App">
      <NavbarComponent session={session} onLogout={handleLogout} />
      <Routes>
        {/* Ruta principal - Muestra los componentes de la landing page */}
        <Route path="/" element={
          <>
            <HeroSection />
            <FeaturedProperties /> {/* Puedes dejar este para propiedades "destacadas" específicas, o vaciarlo */}
            <ServicesSection />
          </>
        } />
        {/* Ruta de Login */}
        <Route path="/login" element={<LoginPage onLoginSuccess={handleLoginSuccess} />} />
        {/* Ruta de Recuperación de Contraseña */}
        <Route path="/recuperar" element={<Recovery />} />

        {/* Rutas de Dashboards (sin cambios aquí) */}
        <Route path="/admin/dashboard" element={<AdminDashboard />} />
        <Route path="/gestor/dashboard" element={<GestorDashboard />} />
        <Route path="/propietario/dashboard" element={<PropietarioDashboard />} />
        <Route path="/dashboard" element={<Dashboard />} />

        {/* ¡NUEVA RUTA PARA MOSTRAR TODAS LAS PROPIEDADES! */}
        {/* Los usuarios pueden navegar a /properties para ver la lista completa */}
        <Route path="/properties" element={<PropertiesList />} />

        {/* Puedes añadir más rutas aquí, por ejemplo, para registro */}
        {/* <Route path="/registro" element={<RegisterPage />} /> */}
      </Routes>
      <FooterComponent session={session} />
    </div>
  );
}

export default App;
