import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Importar componentes de UI (estos no cambian)
import NavbarComponent from './components/NavbarComponent';
import HeroSection from './components/HeroSection';
import FeaturedProperties from './components/FeaturedProperties';
import ServicesSection from './components/ServicesSection';
import FooterComponent from './components/FooterComponent';

// Importar los componentes de autenticación
import LoginPage from './components/Login'; // <--- ASUME QUE EL COMPONENTE DE LOGIN ESTÁ EN 'components/Login.js'
// Si lo guardaste como 'LoginPage.js' en la carpeta 'pages', sería:
// import LoginPage from './pages/LoginPage';
import Recovery from './components/Recovery';

// === Agrega estas importaciones para los componentes de Dashboard ===
// Asegúrate de que las rutas a continuación coincidan con la ubicación real de tus archivos.
// Si aún no los has creado, puedes crear archivos vacíos por ahora con un componente básico.
import AdminDashboard from './components/AdminDashboard';       // <--- Asegúrate que exista en src/components/AdminDashboard.js
import GestorDashboard from './components/GestorDashboard';     // <--- Asegúrate que exista en src/components/GestorDashboard.js
import PropietarioDashboard from './components/PropietarioDashboard'; // <--- Asegúrate que exista en src/components/PropietarioDashboard.js
import Dashboard from './components/Dashboard';                 // <--- Asegúrate que exista en src/components/Dashboard.js
// =================================================================


function App() {
  const [session, setSession] = useState({
    isLoggedIn: false,
    userName: '',
    userType: ''
  });

  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      const user = JSON.parse(storedUser);
      setSession({
        isLoggedIn: true,
        userName: user.nombre,
        userType: user.tipo_usuario
      });
    }
  }, []);

  const handleLoginSuccess = (userData) => {
    setSession({
      isLoggedIn: true,
      userName: userData.nombre,
      userType: userData.tipo_usuario
    });
    localStorage.setItem('user', JSON.stringify(userData));

    switch (userData.tipo_usuario) {
        case 'admin':
            navigate('/admin/dashboard');
            break;
        case 'gestor':
            navigate('/gestor/dashboard');
            break;
        case 'propietario':
            navigate('/propietario/dashboard');
            break;
        default:
            navigate('/dashboard');
    }
  };

  const handleLogout = () => {
    setSession({ isLoggedIn: false, userName: '', userType: '' });
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div className="App">
      <NavbarComponent session={session} onLogout={handleLogout} />
      <Routes>
        <Route path="/" element={
          <>
            <HeroSection />
            <FeaturedProperties />
            <ServicesSection />
          </>
        } />
        <Route path="/login" element={<LoginPage onLoginSuccess={handleLoginSuccess} />} />
        <Route path="/recuperar" element={<Recovery />} />

        {/* Las rutas usan los componentes que acabamos de importar */}
        <Route path="/admin/dashboard" element={<AdminDashboard />} />
        <Route path="/gestor/dashboard" element={<GestorDashboard />} />
        <Route path="/propietario/dashboard" element={<PropietarioDashboard />} />
        <Route path="/dashboard" element={<Dashboard />} />

        {/* Añade más rutas aquí según tus necesidades */}
      </Routes>
      <FooterComponent session={session} />
    </div>
  );
}

export default App;
